// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

contract SplitPayment {
    address public owner;

    constructor(address _owner) {
        owner = _owner;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner can send a Tx!");
        _;
    }

    function send(address[] memory recipients, uint256[] memory amounts) public payable onlyOwner {
        require(recipients.length == amounts.length, "Recipients and amounts length mismatch");
        uint256 total = 0;

        for (uint256 i = 0; i < amounts.length; i++) {
            total += amounts[i];
        }

        require(msg.value == total, "Sent value does not match total amounts");

        for (uint256 i = 0; i < recipients.length; i++) {
            payable(recipients[i]).transfer(amounts[i]);
        }
    }

    // Fallback function to allow receiving Ether
    receive() external payable {}
}
